import { Component, OnInit, ViewChild } from "@angular/core";
import { IgxBannerComponent } from "igniteui-angular";

@Component({
    selector: "banner-sample-1",
    styleUrls: ["banner-sample-1.component.scss"],
    templateUrl: "banner-sample-1.component.html"
})

export class BannerSample1Component implements OnInit {
    @ViewChild(IgxBannerComponent) public banner: IgxBannerComponent;
    public contentWidth = "384px";
    public imageUrls = ["assets/images/card/media/the_red_ice_forest.jpg",
        "assets/images/card/media/yosemite.jpg"];
    public ngOnInit() {
        this.banner.open();
    }
}

.offline-banner {
    border-bottom: 1px solid rgba(0, 0, 0, .12);
}

.gallery__wrapper {
    max-width: 400px;
    padding-top: 24px;
    margin: 0 auto;
}
.gallery__content {
    display: flex;
    flex-flow: column;
    max-height: 440px;
    overflow: hidden;
    background-color: #f2f2f2;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,.2), 0 1px 1px 0 rgba(0,0,0,.14), 0 2px 1px -1px rgba(0,0,0,.12);
}
.gallery__item {
    padding: 16px;
}
.gallery__item:first-child {
    padding-top: 24px;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxBannerModule,
	IgxNavbarModule,
	IgxCardModule,
	IgxIconModule
 } from "igniteui-angular";
import { BannerSample1Component } from "./banner-sample-1/banner-sample-1.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		BannerSample1Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxBannerModule,
		IgxCardModule,
		IgxIconModule,
		IgxNavbarModule,
		IgxIconModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
