import { Component, OnInit } from "@angular/core";
import { Member } from "../model/member.model";

@Component({
  selector: "app-badge-sample-3",
  styleUrls: ["./badge-sample-3.component.css"],
  templateUrl: "./badge-sample-3.component.html"
})
export class BadgeSample3Component implements OnInit {
  public members: Member[] = [
    new Member("Terrance Orta", "online"),
    new Member("Donna Price", "online"),
    new Member("Lisa Landers", "away"),
    new Member("Dorothy H. Spencer", "offline")
  ];
  constructor() { }

  public ngOnInit() {
  }
}

.wrapper {
  display: flex;
  flex-direction: row; }

.contact-name {
  font-weight: 600; }

.contact-container {
  margin-left: 20px; }

.badge-style {
  position: absolute;
  bottom: -6px;
  right: -50px; }

export class Member {
    public name: string;
    public status: string;
    public type: string;
    public icon: string;

    constructor(name: string, status: string) {
        this.name = name;
        this.status = status;
        switch (status) {
            case "online":
                this.type = "success";
                this.icon = "check";
                break;
            case "away":
                this.type = "warning";
                this.icon = "schedule";
                break;
            case "offline":
                this.type = "error";
                this.icon = "remove";
                break;
        }
    }
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxBadgeModule,
	IgxListModule,
	IgxAvatarModule
 } from "igniteui-angular";
import { BadgeSample3Component } from "./badge-sample-3/badge-sample-3.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		BadgeSample3Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxBadgeModule,
		IgxListModule,
		IgxAvatarModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
