import { Component, OnInit, ViewChild } from "@angular/core";
import { IgxBannerComponent } from "igniteui-angular";
// tslint:disable:object-literal-sort-keys
@Component({
  selector: "banner-sample-4",
  templateUrl: "./banner-sample-4.component.html",
  styleUrls: ["./banner-sample-4.component.css"]
})
export class BannerSample4Component implements OnInit {
  public automaticReplies = true;
  public automaticReplyText = `Dear Sender,

I am currently OOO with limited access to e-mails.

I will respond to your as soon as I can.
For urgent matters, please contact qually-t@example.com;

Regards,
Gort`;

  @ViewChild(IgxBannerComponent) public banner: IgxBannerComponent;
  public contentWidth = "384px";
  public ngOnInit() {
    this.banner.open();
  }

  public toggleBanner(event) {
    if (!event) {
      if (!this.banner.collapsed) {
        this.banner.close();
      }
    } else {
      this.banner.open();
    }
  }

  public automaticRepliesSwitch(state: boolean) {
    this.automaticReplies = state;
    this.banner.close();
  }
}

[hidden] {
  display: none !important; }

.settings {
  width: 400px;
  height: 360px;
  margin: 24px auto;
  overflow: hidden;
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.26), 0 2px 2px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.08);
  background: #fff;
  border-radius: 2px; }

igx-banner {
  border-bottom: 1px solid rgba(0, 0, 0, 0.12); }

.profile {
  position: relative;
  display: flex;
  align-items: center;
  padding: 24px 16px;
  background-image: url("assets/images/banner/cort.jpg"); }
  .profile__avatar {
    box-sizing: content-box;
    border: 2px solid rgba(0, 0, 0, 0.24);
    z-index: 2; }
  .profile__detail {
    color: #000000;
    margin-left: 16px;
    z-index: 2; }
  .profile::after {
    position: absolute;
    content: '';
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: #f3f3f3;
    opacity: .64;
    z-index: 1; }

.reply {
  flex-flow: column;
  padding: 0 16px; }
  .reply__switch {
    width: 100%;
    padding: 16px 0;
    justify-content: space-between; }
  .reply__message {
    resize: none;
    height: 14rem; }

