import { Component, ViewChild } from "@angular/core";
import { IgxCalendarComponent } from "igniteui-angular";
import { DateParser} from "../../date-parser";

@Component({
  providers: [DateParser],
  selector: "app-calendar",
  styleUrls: ["./calendar-sample-4.component.css"],
  templateUrl: "./calendar-sample-4.component.html"
})

export class CalendarSample4Component {
  public intlDateTimeFormat = new Intl.DateTimeFormat() as any;
  public formatParts: boolean = this.intlDateTimeFormat.formatToParts;

  constructor(public parser: DateParser) { }

 }

export class DateParser {

    public getDatePart(val: any, component: any, datePart: string) {
        const date = val.date as Date;
        const locale = component.locale;
        const formatOptions: Intl.DateTimeFormatOptions = {};
        formatOptions[datePart] = component.formatOptions[datePart];

        return date.toLocaleString(locale, formatOptions);
    }
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { IgxCalendarModule } from "igniteui-angular";
import { CalendarSample4Component } from "./calendar/calendar-sample-4/calendar-sample-4.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		CalendarSample4Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxCalendarModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
