import { ChangeDetectorRef, Component, OnInit } from "@angular/core";
import { localData } from "./local-data";

@Component({
    selector: "app-combo-template",
    styleUrls: ["./combo-template.component.css"],
    templateUrl: "./combo-template.component.html"
})
export class ComboTemplateComponent implements OnInit {
    public lData: any[];

    constructor(public cdr: ChangeDetectorRef) {}

    public ngOnInit() {
        this.lData = localData;
    }
}

.combo-container {
  margin: 10px; }

.header-class, .footer-class, .empty-class {
  text-align: center;
  font-style: italic;
  font-weight: bold; }

:host {
  display: block;
  overflow: auto;
  height: 100%; }

const division = {
    "New England 01": ["Connecticut", "Maine", "Massachusetts"],
    "New England 02": ["New Hampshire", "Rhode Island", "Vermont"],
    // tslint:disable-next-line:object-literal-sort-keys
    "Mid-Atlantic": ["New Jersey", "New York", "Pennsylvania"],
    "East North Central 02": ["Michigan", "Ohio", "Wisconsin"],
    "East North Central 01": ["Illinois", "Indiana"],
    "West North Central 01": ["Missouri", "Nebraska", "North Dakota", "South Dakota"],
    "West North Central 02": ["Iowa", "Kansas", "Minnesota"],
    "South Atlantic 01": ["Delaware", "Florida", "Georgia", "Maryland"],
    "South Atlantic 02": ["North Carolina", "South Carolina", "Virginia",
        "District of Columbia", "West Virginia"],
    "South Atlantic 03": ["District of Columbia", "West Virginia"],
    "East South Central 01": ["Alabama", "Kentucky"],
    "East South Central 02": ["Mississippi", "Tennessee"],
    "West South Central": ["Arkansas", "Louisiana", "Oklahome", "Texas"],
    "Mountain": ["Arizona", "Colorado", "Idaho", "Montana", "Nevada", "New Mexico", "Utah", "Wyoming"],
    "Pacific 01": ["Alaska", "California"],
    "Pacific 02": ["Hawaii", "Oregon", "Washington"]
};

const localData: any[] = [];
const keys = Object.keys(division);
for (const key of keys) {
    division[key].map((e) => {
        localData.push({
            field: e,
            region: key.substring(0, key.length - 3)
        });
    });
}

export { localData };

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { IgxComboModule } from "igniteui-angular";
import { ComboTemplateComponent } from "./combo/combo-template/combo-template.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ComboTemplateComponent
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxComboModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
