import { Component, ViewChild } from "@angular/core";
import {
    IgxExpansionPanelComponent
} from "igniteui-angular";
@Component({
    // tslint:disable-next-line:component-selector
    selector: "app-expansion-sample-1",
    styleUrls: ["./expansion-sample-1.component.css"],
    templateUrl: "./expansion-sample-1.component.html"
})
export class ExpansionPanelSample1Component {
    @ViewChild(IgxExpansionPanelComponent, { read: IgxExpansionPanelComponent })
    public panel: IgxExpansionPanelComponent;
    public readMore = "https://en.wikipedia.org/wiki/Hummingbird";
}

.igx-expansion-panel {
  font-family: Verdana;
  margin: 24px;
  width: 420px;
  box-shadow: 0 1px 5px 0 rgba(0, 0, 0, 0.26), 0 2px 2px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.08); }

.igx-expansion-panel__header--expanded {
  border-bottom: 1px dashed rgba(0, 0, 0, 0.74); }

.igx-expansion-panel__body > * {
  padding: 8px 16px; }

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxIconModule,
	IgxExpansionPanelModule
 } from "igniteui-angular";
import { ExpansionPanelSample1Component } from "./expansion-sample-1/expansion-sample-1.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ExpansionPanelSample1Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxIconModule,
		IgxExpansionPanelModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
