import { Component, ViewChild } from "@angular/core";
import { IgxExpansionPanelComponent } from "igniteui-angular";
import { data as weatherData } from "./weather-data";
@Component({
    // tslint:disable-next-line:component-selector
    selector: "app-expansion-sample-7",
    styleUrls: ["./expansion-sample-7.component.css"],
    templateUrl: "./expansion-sample-7.component.html"
})
export class ExpansionPanelSample7Component {

    @ViewChild(IgxExpansionPanelComponent)
    public panel: IgxExpansionPanelComponent;
    public data = weatherData;

    public toggleDetails() {
        this.panel.toggle();
    }
}

@import url("https://unpkg.com/@fortawesome/fontawesome-free-webfonts@^1.0.9/css/fontawesome.css");
@import url("https://unpkg.com/@fortawesome/fontawesome-free-webfonts@^1.0.9/css/fa-regular.css");
@import url("https://unpkg.com/@fortawesome/fontawesome-free-webfonts@^1.0.9/css/fa-solid.css");
.sample-wrapper {
  width: 24rem; }

.forecast__day, .weather__main-temp, .weather__main-hum {
  display: flex; }
  .forecast__day > *, .weather__main-temp > *, .weather__main-hum > * {
    flex: 1 1 auto; }
  .forecast__day igx-icon, .weather__main-temp igx-icon, .weather__main-hum igx-icon {
    font-size: 1.5em;
    margin-right: 1rem; }

.forecast__day {
  padding-top: 1rem; }

.right {
  text-align: right; }

.weather__main-temp {
  font-size: 4rem;
  line-height: 4rem; }
  .weather__main-temp igx-icon {
    font-size: 4rem;
    width: 4rem;
    height: 4rem; }

.weather__main-hum {
  padding: .7rem 0; }

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxIconModule,
	IgxCardModule,
	IgxExpansionPanelModule,
	IgxButtonModule,
	IgxRippleModule
 } from "igniteui-angular";
import { ExpansionPanelSample7Component } from "./expansion-sample-7/expansion-sample-7.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ExpansionPanelSample7Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxIconModule,
		IgxExpansionPanelModule,
		IgxCardModule,
		IgxButtonModule,
		IgxRippleModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
