@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ExcelExportComponent
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,

  ],
  providers: [IgxExcelExporterService],
  entryComponents: []
})
export class AppModule {}
