import { Component, ElementRef, Inject, ViewChild } from "@angular/core";
import {
    AbsoluteScrollStrategy, AutoPositionStrategy, BlockScrollStrategy,
    CloseScrollStrategy, ConnectedPositioningStrategy, GlobalPositionStrategy,
    HorizontalAlignment, IgxCardModule, IgxIconModule, IgxOverlayService,
    IgxSwitchModule, IScrollStrategy, NoOpScrollStrategy, OverlaySettings, PositionSettings, VerticalAlignment
} from "igniteui-angular";
// tslint:disable:object-literal-sort-keys
@Component({
    selector: "overlay-sample",
    styleUrls: ["./overlay-scroll-sample-1.component.css"],
    templateUrl: "./overlay-scroll-sample-1.component.html",
    providers: [IgxOverlayService]
})
export class OverlayScrollSample1Component {

    public modalValue = true;

    @ViewChild("modalDemo")
    public modalDemo: ElementRef;

    @ViewChild("overlayDemo")
    public overlayDemo: ElementRef;

    @ViewChild("mainContainer")
    public mainContainer: ElementRef;

    private _defaultPositionSettings: PositionSettings = {
        target: null,
        horizontalDirection: HorizontalAlignment.Center,
        horizontalStartPoint: HorizontalAlignment.Center,
        verticalDirection: VerticalAlignment.Middle,
        verticalStartPoint: VerticalAlignment.Middle
    };

    private _overlaySettings: OverlaySettings = {
        positionStrategy: new GlobalPositionStrategy(),
        scrollStrategy: new AbsoluteScrollStrategy(),
        modal: true,
        closeOnOutsideClick: true
    };
    constructor(
        @Inject(IgxOverlayService) public overlay: IgxOverlayService
    ) { }

    public onClickModal(event: Event, strat: string) {
        event.stopPropagation();
        const positionSettings = Object.assign(Object.assign({}, this._defaultPositionSettings), {
            target: this.modalDemo.nativeElement,
            horizontalDirection: HorizontalAlignment.Right,
            horizontalStartPoint: HorizontalAlignment.Right,
            verticalDirection: VerticalAlignment.Bottom,
            verticalStartPoint: VerticalAlignment.Bottom
        });
        let positionStrategy;
        switch (strat) {
            case ("auto"):
                positionStrategy = new AutoPositionStrategy(positionSettings);
                break;
            case ("connected"):
                positionStrategy = new ConnectedPositioningStrategy(positionSettings);
                break;
            default:
                positionStrategy = new GlobalPositionStrategy(Object.assign(positionSettings, {
                    horizontalDirection: HorizontalAlignment.Center,
                    verticalDirection: VerticalAlignment.Middle
                }));
        }
        const showSettings = Object.assign(Object.assign({}, this._overlaySettings), {
            modal: this.modalValue,
            positionStrategy
        });
        this.overlay.show(this.overlayDemo, showSettings);
    }
}

.overlay-sample {
  padding: 32px 0px 32px 32px;
  overflow-y: scroll;
  display: flex;
  flex-direction: row;
  height: 1600px;
  margin-bottom: 500px; }

.container__row > div, .section__container--wide > span, .section__position-element > div {
  display: inline-block; }

.section__row--wide, .container__row, .section__header {
  width: 100%; }

.section__row > span {
  height: 48px; }

.section__row--wide, .section__container--wide > span {
  height: 80px;
  max-width: 320px; }

span:hover {
  border: 1px solid #e41c77;
  box-sizing: border-box;
  cursor: pointer; }

.section__container--wide {
  align-content: flex-start;
  display: flex; }

.section__container--wide > span {
  text-align: center;
  vertical-align: middle;
  line-height: 12px;
  font-weight: 500; }

.section__container--wide > span {
  width: 80px;
  padding-top: 16px; }

.section__position-element, .section__position-header {
  width: 144px;
  text-align: center; }

.section__separator {
  width: 32px; }

.container {
  width: 50%;
  display: flex;
  flex-direction: column;
  align-items: center; }

.overlay__element {
  width: 288px;
  height: 144px; }

.overlay__element > .overlay__element__content {
  margin-left: 10%;
  margin-top: 20%;
  width: 80%;
  height: 60%;
  background-color: #a3a3a3;
  font-weight: 800;
  font-size: 24px;
  line-height: 24px;
  box-shadow: 1px 1px #3a3a3a;
  text-shadow: 0.2px 0.2px #3a3a3a; }

.section__header {
  max-height: 96px;
  text-align: left;
  font-weight: 700;
  font-size: 16px; }

.container__row {
  height: 100%;
  text-align: left;
  margin-top: 16px; }

.section__position-element, .section__row--wide {
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.2), 0 1px 1px 0 rgba(0, 0, 0, 0.14), 0 2px 1px -1px rgba(0, 0, 0, 0.12); }

.igx-switch {
  padding: 16px; }

