import { Component } from "@angular/core";
import { FormBuilder, FormControl, FormGroup, Validators } from "@angular/forms";

@Component({
    selector: "app-combo",
    styleUrls: ["./reactive-forms.component.css"],
    templateUrl: "./reactive-forms.component.html"
})
export class ReactiveFormsSampleComponent {
    public dsGenres = ["Action", "Adventure", "Comedy", "Crime",
        "Drama", "Historical", "Horror", "Mistery",
        "Musical", "Romance", "Sci-Fi", "Thriller", "Western"];

    public user: FormGroup;
    constructor(fb: FormBuilder) {
        this.user = fb.group({
            date: [""],
            dateTime: [""],
            email: ["", Validators.required],
            fullName: new FormControl("", Validators.required),
            genres: ["", Validators.required],
            phone: [""]
        });
    }

    public onSubmit() {
        console.log(this.user);
    }
}

.igx-input-group + .igx-input-group, .button-wrapper, .igx-datePicker, .igx-time-picker, .igx-combo {
  margin-top: 32px; }

.input-group-form {
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.2), 0 1px 1px 0 rgba(0, 0, 0, 0.14), 0 2px 1px -1px rgba(0, 0, 0, 0.12);
  padding: 24px 16px;
  margin-bottom: 48px; }

.sample-title {
  margin-top: 0; }

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxIconModule,
	IgxInputGroupModule,
	IgxButtonModule,
	IgxRippleModule,
	IgxDatePickerModule,
	IgxTimePickerModule,
	IgxComboModule
 } from "igniteui-angular";
import { ReactiveFormsSampleComponent } from "./reactive-forms/reactive-forms.component";
import { 
	FormsModule,
	ReactiveFormsModule
 } from "@angular/forms";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ReactiveFormsSampleComponent
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxIconModule,
		IgxInputGroupModule,
		IgxButtonModule,
		IgxRippleModule,
		IgxDatePickerModule,
		IgxTimePickerModule,
		IgxComboModule,
		FormsModule,
		ReactiveFormsModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
