  constructor() { }

}

.tabbar-wrapper {
  position: relative;
  height: 540px;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.2), 0 1px 1px 0 rgba(0, 0, 0, 0.14), 0 2px 1px -1px rgba(0, 0, 0, 0.12);
  overflow: hidden; }

.item {
  margin-bottom: 5px; }

.item-line1 {
  font-size: 14px;
  color: gray; }

.item-line2 {
  font-size: 12px;
  color: darkgray; }

