import { 
	IgxDropDownModule,
	IgxIconModule,
	IgxNavbarModule,
	IgxToggleModule
 } from "igniteui-angular";
import { DropdownMenuComponent } from "./dropdown-menu/dropdown-menu.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		DropdownMenuComponent
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxDropDownModule,
		IgxIconModule,
		IgxNavbarModule,
		IgxToggleModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
