import { Component, OnInit, ViewChild } from "@angular/core";
import {
    ConnectedPositioningStrategy,
    IgxDropDownComponent,
    IgxInputDirective,
    IgxInputGroupComponent,
    ISelectionEventArgs
} from "igniteui-angular";
@Component({
    // tslint:disable-next-line:component-selector
    selector: "app-dropdown-sample-4",
    styleUrls: ["./dropdown-sample-4.component.scss"],
    templateUrl: "./dropdown-sample-4.component.html"
})
export class DropDownSample4Component implements OnInit {
    @ViewChild(IgxDropDownComponent) public igxDropDown: IgxDropDownComponent;
    @ViewChild("inputGroup", { read: IgxInputGroupComponent}) public inputGroup: IgxInputGroupComponent;
    @ViewChild("input", { read: IgxInputDirective })
    public input: IgxInputDirective;

    public items: any[] = [];
    public value: string;

    public ngOnInit() {
        for (let i = 1; i < 4; i ++) {
            const item = { field: "Option " + i };
            this.items.push(item);
        }
    }

    public onSelection(eventArgs: ISelectionEventArgs) {
        this.value = eventArgs.newSelection.value;
    }

    public openDropDown() {
        if (this.igxDropDown.collapsed) {
            this.igxDropDown.open({
                modal: false,
                positionStrategy: new ConnectedPositioningStrategy({
                    target: this.inputGroup.element.nativeElement
                })
            });
        }
    }
}

.sample-wrapper {
    padding-left: 10px;
}

.input {
    padding-left: 0.2rem;
}

.input-group {
    width: 160px;
}

.igx-input-group {
    display: inline-block;
}

span {
    display: inline;
    color: #e41c77;
    margin-left: 20px;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxDropDownModule,
	IgxInputGroupModule,
	IgxRippleModule,
	IgxIconModule,
	IgxToggleModule
 } from "igniteui-angular";
import { DropDownSample4Component } from "./dropdown-sample-4/dropdown-sample-4.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		DropDownSample4Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxDropDownModule,
		IgxInputGroupModule,
		IgxRippleModule,
		IgxIconModule,
		IgxToggleModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
