import { Component, ViewChild } from "@angular/core";
import {
    IgxExpansionPanelComponent, IgxToastComponent
} from "igniteui-angular";
@Component({
    // tslint:disable-next-line:component-selector
    selector: "app-expansion-sample-3",
    styleUrls: ["./expansion-sample-3.component.scss"],
    templateUrl: "./expansion-sample-3.component.html"
})
export class ExpansionPanelSample3Component {
    @ViewChild(IgxExpansionPanelComponent, { read: IgxExpansionPanelComponent })
    public panel: IgxExpansionPanelComponent;
    @ViewChild(IgxToastComponent, { read: IgxToastComponent })
    public eventToast: IgxToastComponent;
    public readMore = "https://en.wikipedia.org/wiki/Hummingbird";
    // tslint:disable-next-line:max-line-length
    public imgSource = "https://upload.wikimedia.org/wikipedia/commons/4/46/Purple-throated_carib_hummingbird_feeding.jpg";
    public handleExpansion(evt?: {event: Event}) {
        this.eventToast.hide();
        console.log("Expanded event thrown ", (evt ? "with event: " + evt.event : "with no event"));
        this.eventToast.message = "Expanded Event Fired!";
        this.eventToast.show();
    }

    public handleCollapse(evt?: {event: Event}) {
        this.eventToast.hide();
        console.log("Collapsed event thrown ", (evt ? "with event: " + evt.event : "with no event"));
        this.eventToast.message = "Collapsed Event Fired!";
        this.eventToast.show();
    }
}

.igx-expansion-panel {
    font-family: Verdana;
}

.example-icon {
    font-size: 12px;
    font-weight: 600;
    text-shadow: 1px #000;
}

igx-expansion-panel-title {
    font-weight: 700;
    font-size: 18px;
}

igx-expansion-panel-description {
    font-weight: 400;
    font-size: 16px;
}

.igx-expansion-panel {
    font-family: Verdana;
    margin: 24px;
    width: 420px;
    box-shadow: 0 1px 5px 0 rgba(0, 0, 0, .26), 0 2px 2px 0 rgba(0, 0, 0, .12), 0 3px 1px -2px rgba(0, 0, 0, .08);
}

.igx-expansion-panel__header--expanded {
    border-bottom: 1px dashed rgba(0, 0, 0, .74);
}

.igx-expansion-panel__body > * {
    padding: 8px 16px;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxIconModule,
	IgxAvatarModule,
	IgxToastModule,
	IgxExpansionPanelModule
 } from "igniteui-angular";
import { ExpansionPanelSample3Component } from "./expansion-sample-3/expansion-sample-3.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ExpansionPanelSample3Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxIconModule,
		IgxAvatarModule,
		IgxToastModule,
		IgxExpansionPanelModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
