import { useAnimation } from "@angular/animations";
import { Component, ViewChild } from "@angular/core";

import {
    IgxExpansionPanelComponent,
    slideInLeft,
    slideOutRight
} from "igniteui-angular";

@Component({
    // tslint:disable-next-line:component-selector
    selector: "app-expansion-sample-6",
    styleUrls: ["./expansion-sample-6.component.scss"],
    templateUrl: "./expansion-sample-6.component.html"
})
export class ExpansionPanelSample6Component {

    @ViewChild(IgxExpansionPanelComponent)
    public panel: IgxExpansionPanelComponent;

    public animationSettingsCustom = {
        closeAnimation: useAnimation(slideOutRight, {
                params: {
                    duration: "100ms",
                    toPosition: "translateX(25px)"
            }
        }),
        openAnimation: useAnimation(slideInLeft, {
                params: {
                    duration: "500ms",
                    fromPosition: "translateX(-15px)",
                    startOpacity: 0.1
                }
            })
    };

    public user = {
        email: "",
        fullName: "",
        phone: ""
    };

    public collapsed() {
        return this.panel && this.panel.collapsed;
    }
}

.sample-wrapper {
    width: 420px;
}

.content {
    width: 100%;
}

.content__collapsible {
    width: 100%;
    border: 1px solid hsl(0, 0%, 82%);
    border-radius: .3em;
    box-shadow: 0 1px 2px hsl(0, 0%, 82%);
}

.body {
    padding: 0em 1em;
}

.group {
    padding: 0em 0em 1em 0px;
}

.sample-title {
    font-weight: 900;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxIconModule,
	IgxExpansionPanelModule,
	IgxInputGroupModule
 } from "igniteui-angular";
import { ExpansionPanelSample6Component } from "./expansion-sample-6/expansion-sample-6.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ExpansionPanelSample6Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxIconModule,
		IgxExpansionPanelModule,
		IgxInputGroupModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
