import { Component, OnInit, ViewChild, ViewEncapsulation} from "@angular/core";
import {
    IgxColumnComponent,
    IgxGridComponent,
    IgxNumberSummaryOperand,
    IgxSummaryResult } from "igniteui-angular";
import { LOCAL_DATA } from "./data";

class MySummary extends IgxNumberSummaryOperand {

  constructor() {
    super();
  }

  public operate(data?: any[]): IgxSummaryResult[] {
    const result = super.operate(data);
    result.push({
      key: "test",
      label: "Test",
      summaryResult: data.filter((rec) => rec > 10 && rec < 30).length
    });

    return result;
  }
}
@Component({
  encapsulation: ViewEncapsulation.None,
  selector: "app-grid-sample-3",
  styleUrls: ["./grid-sample-3.component.scss"],
  templateUrl: "./grid-sample-3.component.html"
})
export class GridSample3Component implements OnInit {

  @ViewChild("grid1", { read: IgxGridComponent })
  public grid1: IgxGridComponent;
  public mySummary = MySummary;
  public data;
  public productId = 0;

  constructor() {
    this.data = LOCAL_DATA;
    this.productId = LOCAL_DATA.length;
   }

  public ngOnInit() {
  }
  public toggleSummary(column: IgxColumnComponent) {
    column.hasSummary = !column.hasSummary;
  }
}

.grid-controls {
    display: flex;
    flex-flow: column nowrap;
    justify-content: space-between;
    margin: 0 16px 24px;

    igx-switch {
        margin-top: 24px;
    }

}

.grid__wrapper {
    margin: 0 16px;
}

.header {
    height: 100%;
}

.igx-grid__th .title{
    width: 100%;
    cursor: auto;
}

@media screen and (max-width: 677px){

    [class*=header-icon]{
        padding-bottom: 17px;
        padding-top: 17px;
        font-size: 1.4em;
        width: 1.1em;
        height: 1.1em;
        float: right;
    }
    [class*=text]{
        float:left;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        width: 50%;
    }  
}


@media screen and (min-width: 677px){

    [class*=header-icon]{
        padding-top: 17px;
        font-size: 1.4em;
        width: 1.1em;
        height: 1.1em;
        float: right;
    }

    [class*=text] {
        float:left;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        width: 50%;
    }    
}


@media screen and (min-width: 992px){

    [class*=header-icon] {
        padding-top: 17px;
        font-size: 1.4em;
        width: 1.1em;
        height: 1.1em;
        float: right;
        margin-right: 10px;
        cursor: pointer;
    }

    [class*=text] {
        float:left;
        white-space: nowrap;
        width: 50%;
        cursor: auto;
    }
}

