import { Component, OnInit } from "@angular/core";
import { IgxFilterOptions } from "igniteui-angular";
@Component({
  selector: "app-contact-list2",
  styleUrls: ["./list-sample-4.component.scss"],
  templateUrl: "./list-sample-4.component.html"
})
export class ListSample4Component implements OnInit {
  public searchContact: string;

  public contacts = [
    {
      isFavorite: false,
      name: "Terrance Orta",
      phone: "770-504-2217",
      photo: "assets/images/men/27.jpg"
    },
    {
      isFavorite: true,
      name: "Richard Mahoney",
      phone: "423-676-2869",
      photo: "assets/images/men/1.jpg"
    },
    {
      isFavorite: false,
      name: "Donna Price",
      phone: "859-496-2817",
      photo: "assets/images/women/50.jpg"
    },
    {
      isFavorite: false,
      name: "Lisa Landers",
      phone: "901-747-3428",
      photo: "assets/images/women/3.jpg"
    },
    {
      isFavorite: true,
      name: "Dorothy H. Spencer",
      phone: "573-394-9254",
      photo: "assets/images/women/67.jpg"
    }
  ];

  constructor() { }

  public ngOnInit() { }

  public toggleFavorite(contact: any) {
    contact.isFavorite = !contact.isFavorite;
  }

  get filterContacts() {
    const fo = new IgxFilterOptions();
    fo.key = "name";
    fo.inputValue = this.searchContact;
    return fo;
  }
}

:host {
    display: block;
    padding: 16px;
}

.list-sample {
    box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.2),
        0px 1px 1px 0px rgba(0, 0, 0, 0.14),
        0px 2px 1px -1px rgba(0, 0, 0, 0.12);
}

igx-icon {
  cursor: pointer;
}

.item-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.contact {
  display: flex;
  flex: 1 0 240px;
  align-items: center;

  &__info {
    display: flex;
    flex-flow: column nowrap;
    margin-left: 24px;
  }
}

.name {
  font-weight: 600;
}

.phone {
  font-size: 0.875em;
  line-height: 1;
}

.search {
    margin-bottom: 16px;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxAvatarModule,
	IgxFilterModule,
	IgxIconModule,
	IgxListModule,
	IgxInputGroupModule
 } from "igniteui-angular";
import { ListSample4Component } from "./list-sample-4/list-sample-4.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ListSample4Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxAvatarModule,
		IgxFilterModule,
		IgxIconModule,
		IgxListModule,
		IgxInputGroupModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
