import { Component, OnInit, ViewChild } from "@angular/core";

import { IgxListComponent, IgxToastComponent } from "igniteui-angular";

@Component({
    selector: "app-contact-list2",
    styleUrls: ["./list-sample-7.component.scss"],
    templateUrl: "./list-sample-7.component.html"
})
export class ListSample7Component implements OnInit {

    public contacts;

    @ViewChild("toast")
    public toast: IgxToastComponent;

    @ViewChild("mainIgxList")
    public list: IgxListComponent;

    private dataSource = [{
        isFavorite: false,
        name: "Terrance Orta",
        phone: "770-504-2217",
        photo: "assets/images/men/27.jpg"
    }, {
        isFavorite: true,
        name: "Richard Mahoney",
        phone: "423-676-2869",
        photo: "assets/images/men/1.jpg"
    }, {
        isFavorite: false,
        name: "Donna Price",
        phone: "859-496-2817",
        photo: "assets/images/women/50.jpg"
    }, {
        isFavorite: false,
        name: "Lisa Landers",
        phone: "901-747-3428",
        photo: "assets/images/women/3.jpg"
    }, {
        isFavorite: true,
        name: "Dorothy H. Spencer",
        phone: "573-394-9254",
        photo: "assets/images/women/67.jpg"
    }
    ];

    constructor() {
    }

    public ngOnInit() {
        this.contacts = Object.assign([], this.dataSource);
    }

    public toggleFavorite(contact: any) {
        contact.isFavorite = !contact.isFavorite;
    }

    public rightPanPerformed(args) {
        args.keepItem = true;
        this.toast.message = "Dialing " + this.contacts[args.item.index - 1].name;
        this.toast.show();
    }

    public leftPanPerformed(args) {
        args.keepItem = false;
        setTimeout((idx = args.item.index - 1) => {
            this.toast.message = "Contact " + this.contacts[idx].name + " removed.";
            this.toast.show();
            this.contacts.splice(idx, 1);
        }, 500);
    }

    public repopulateHandler() {
        this.contacts = Object.assign([], this.dataSource);
    }

    public get panThreshold() {
        const result = this.list.panEndTriggeringThreshold;
        return Math.round(result * 100) + "%";
    }

}

:host {
    display: block;
    padding: 16px;
}

.list-sample {
    box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.2),
        0px 1px 1px 0px rgba(0, 0, 0, 0.14),
        0px 2px 1px -1px rgba(0, 0, 0, 0.12);
}

igx-icon {
  cursor: pointer;
}

.item-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.contact {
  display: flex;
  flex: 1 0 240px;
  align-items: center;

  &__info {
    display: flex;
    flex-flow: column nowrap;
    margin-left: 24px;
  }
}

.name {
  font-weight: 600;
}

.phone {
  font-size: 0.875em;
  line-height: 1;
}

.search {
    margin-bottom: 16px;
}

.listItemLeftPanningStyle {
    display: flex;
    flex-direction: row-reverse;
    background-color:orange;
    color: white;
    width: 100%;
    padding-right: 10px;
    align-items: center;
}

.listItemRightPanningStyle {
    display: flex;
    flex-direction: row;
    background-color:limegreen;
    color: white;
    width: 100%;
    padding-left: 10px;
    align-items: center;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxAvatarModule,
	IgxIconModule,
	IgxListModule,
	IgxSliderModule,
	IgxToastModule
 } from "igniteui-angular";
import { ListSample7Component } from "./list-sample-7/list-sample-7.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		ListSample7Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxAvatarModule,
		IgxIconModule,
		IgxListModule,
		IgxSliderModule,
		IgxToastModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
