import { Component, ElementRef, Inject, OnInit, ViewChild } from "@angular/core";
import {
    AbsoluteScrollStrategy, BlockScrollStrategy, CloseScrollStrategy, ConnectedPositioningStrategy,
    IgxCardModule, IgxIconModule, IgxOverlayService, IgxSwitchModule, NoOpScrollStrategy
} from "igniteui-angular";
import { MyDynamicCardComponent } from "../overlay-dynamic-card/overlay-dynamic-card.component";
// tslint:disable:object-literal-sort-keys
@Component({
    selector: "overlay-sample",
    styleUrls: ["./overlay-scroll-sample-2.component.scss"],
    templateUrl: "./overlay-scroll-sample-2.component.html",
    providers: [IgxOverlayService]
})
export class OverlayScrollSample2Component implements OnInit {
    public previewHidden = false;
    @ViewChild("scrollDemo")
    public scrollDemo: ElementRef;

    @ViewChild(MyDynamicCardComponent)
    public overlayDemo: MyDynamicCardComponent;

    @ViewChild("mainContainer")
    public mainContainer: ElementRef;

    constructor(
        @Inject(IgxOverlayService) public overlay: IgxOverlayService
    ) { }

    public ngOnInit(): void {
        (this.mainContainer.nativeElement as HTMLElement).style.height = "450px";
        this.overlay.onOpening.subscribe(() => {
            this.previewHidden = true;
        });
        this.overlay.onClosing.subscribe(() => {
            this.previewHidden = false;
        });
    }

    public onClickScrollStrategy(event: Event, strat: string) {
        let scrollStrategy;
        const positionStrategy = new ConnectedPositioningStrategy();
        switch (strat) {
            case ("absolute"):
                scrollStrategy = new AbsoluteScrollStrategy();
                positionStrategy.settings.target = this.scrollDemo.nativeElement.children[0];
                break;
            case ("block"):
                scrollStrategy = new BlockScrollStrategy();
                positionStrategy.settings.target = this.scrollDemo.nativeElement.children[1];
                break;
            case ("close"):
                scrollStrategy = new CloseScrollStrategy(this.mainContainer.nativeElement);
                positionStrategy.settings.target = this.scrollDemo.nativeElement.children[2];
                break;
            default:
                scrollStrategy = new NoOpScrollStrategy();
                positionStrategy.settings.target = this.scrollDemo.nativeElement.children[3];
        }
        this.overlay.show(MyDynamicCardComponent, {
            positionStrategy,
            scrollStrategy,
            modal: false,
            closeOnOutsideClick: true
        });
    }
}

body {
    overflow-y: scroll !important;
}

.overlay-sample {
    padding: 32px 0px 32px 32px;
    overflow-y: scroll;
    display: flex;
    flex-direction: row;
    height: 200px;
    overflow-y: scroll;
}


.container__row > div, .section__container--wide > span, .section__position-element > div {
    display: inline-block;
}
.section__row--wide, .container__row, .section__header {
    width: 100%;
}

.section__row > span {
    height: 48px;
}
.section__row--wide, .section__container--wide > span {
    height: 80px;
    max-width: 320px;
}

span:hover {
    border: 1px solid #e41c77;
    box-sizing: border-box;
    cursor: pointer;
}

.section__container--wide {
    align-content: flex-start;
    display: flex;;
}

.section__container--wide > span {
    text-align: center;
    vertical-align: middle;
    line-height: 12px;
    font-weight: 500;
}

.section__container--wide > span {
    width: 80px;
    padding-top: 16px;
}

.section__position-element, .section__position-header {
    width: 144px;
    text-align: center;
}

.section__separator {
    width: 32px;
}
.container {
    width: 50%;
    height: 600px;
    display: flex;
    flex-direction: column;
    align-items: center;
}

.overlay__element {
    width: 288px;
    height: 144px;
}

.overlay__element > .overlay__element__content {
    margin-left: 10%;
    margin-top: 20%;
    width: 80%;
    height: 60%;
    background-color: #a3a3a3;
    font-weight: 800;
    font-size: 24px;
    line-height: 24px;
    box-shadow: 1px 1px #3a3a3a;
    text-shadow: 0.2px 0.2px #3a3a3a;
}
.section__header {
    max-height: 96px;
    text-align: left;
    font-weight: 700;
    font-size: 16px;
}

.container__row {
    height: 100%;
    text-align: left;
    margin-top: 16px;
}
.section__position-element, .section__row--wide {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.2), 0 1px 1px 0 rgba(0, 0, 0, 0.14), 0 2px 1px -1px rgba(0, 0, 0, 0.12);
}

import { Component, ElementRef } from "@angular/core";
import { IgxCardModule } from "igniteui-angular";
// tslint:disable:object-literal-sort-keys
@Component({
    selector: `overlay-dynamic-card-component`,
    templateUrl: `overlay-dynamic-card.component.html`,
    styleUrls: [`overlay-dynamic-card.component.scss`]
})
export class MyDynamicCardComponent {
    constructor(public elementRef: ElementRef) {}
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxIconModule,
	IgxOverlayService,
	IgxSwitchModule,
	IgxCardModule
 } from "igniteui-angular";
import { OverlayScrollSample2Component } from "./overlay/overlay-scroll-2/overlay-scroll-sample-2.component";
import { MyDynamicCardComponent } from "./overlay/overlay-dynamic-card/overlay-dynamic-card.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		OverlayScrollSample2Component,
		MyDynamicCardComponent
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxIconModule,
		IgxSwitchModule,
		IgxCardModule
  ],
  providers: [IgxOverlayService],
  entryComponents: [MyDynamicCardComponent]
})
export class AppModule {}
