import { Component } from "@angular/core";

@Component({
    selector: "app-radio-sample-2",
    styleUrls: ["./radio-sample-2.component.scss"],
    templateUrl: "./radio-sample-2.component.html"
})
export class RadioSample2Component {
    public title = "Select Background";

    public colors = [{
        hex: "#f06a2f",
        name: "Carrot"
    }, {
        hex: "#ff134a",
        name: "Watermelon"
    }, {
        hex: "#7bc96f",
        name: "Grass"
    },
    {
        hex: "transparent",
        name: "No color"
    }];

    public selectedColor: string = this.colors[3].hex;
}

:host {
    display: flex;
    flex-flow: column nowrap;
    padding: 16px;
}

igx-radio + igx-radio {
    margin-top: 16px;
}

.box {
    width: 100%;
    height: 200px;
    margin-top: 24px;
    border-radius: 4px;
    box-shadow: 0px 2px 6px rgba(0, 0, 0, .24);
    transition: background-color .2s ease-out;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { IgxRadioModule } from "igniteui-angular";
import { RadioSample2Component } from "./radio-sample-2/radio-sample-2.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		RadioSample2Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxRadioModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
