import { Component, OnInit } from "@angular/core";
import { SliderType } from "igniteui-angular";

@Component({
  selector: "app-slider-sample-1",
  styleUrls: ["./slider-sample-1.component.scss"],
  templateUrl: "./slider-sample-1.component.html"
})
export class SliderSample1Component implements OnInit {
  public sliderType = SliderType;
  public priceRange: PriceRange = new PriceRange(200, 800);

  constructor() { }

  public updatePriceRange(event) {
    const prevPriceRange = this.priceRange;
    switch (event.id) {
      case "lowerInput": {
        if (!isNaN(parseInt(event.value, 10))) {
          this.priceRange = new PriceRange(event.value, prevPriceRange.upper);
        }
        break;
      }
      case "upperInput": {
        if (!isNaN(parseInt(event.value, 10))) {
          this.priceRange = new PriceRange(prevPriceRange.lower, event.value);
        }
        break;
      }
    }
  }

  public ngOnInit() {
  }
}

class PriceRange {
  constructor(
    public lower: number,
    public upper: number
  ) {
  }
}

.container {
    margin: 30px 20px;
}
.wrapper {
    display: flex;
    justify-content: space-between;
    flex-direction: row;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxInputGroupModule,
	IgxSliderModule
 } from "igniteui-angular";
import { SliderSample1Component } from "./slider-sample-1/slider-sample-1.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		SliderSample1Component
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxInputGroupModule,
		IgxSliderModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
