import { Component, OnInit, ViewChild } from "@angular/core";
import { IgxSnackbarComponent } from "igniteui-angular";

@Component({
  selector: "app-snackbar-sample-4",
  styleUrls: ["./snackbar-sample-4.component.scss"],
  templateUrl: "./snackbar-sample-4.component.html"
})
export class SnackbarSample4Component implements OnInit {
  @ViewChild(IgxSnackbarComponent)
  public snackbar: IgxSnackbarComponent;
  public navItems: any[];
  public deletedItems = [];

  constructor() { }

  public ngOnInit() {
    this.navItems = [{
      avatar: "assets/images/avatar/2.jpg",
      text: "Richard Mahoney"
    },
    {
      avatar: "assets/images/avatar/4.jpg",
      text: "Lisa Landers"
    },
    {
      avatar: "assets/images/avatar/14.jpg",
      text: "Marianne Taylor"
    }, {
      avatar: "assets/images/avatar/17.jpg",
      text: "Ward Riley"
    }];
  }

  public delete(item) {
    this.deletedItems.push([item, this.navItems.indexOf(item)]);
    this.navItems.splice(this.navItems.indexOf(item), 1);
    this.snackbar.show();
  }

  public restore() {
    const [item, index] = this.deletedItems.pop();
    this.navItems.splice(index, 0, item);
    this.snackbar.hide();
  }
}

:host {
    display: block;
    padding: 16px;
}

.list-sample {
    display: block;
    position: relative;
    overflow: hidden;
    height: 280px;
    min-width: 300px;
    box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.2), 0px 1px 1px 0px rgba(0, 0, 0, 0.14), 0px 2px 1px -1px rgba(0, 0, 0, 0.12);
}

.item-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.contact {
  display: flex;
  flex: 1 0 240px;
  align-items: center;
  position: relative;
  z-index: 0;

  &__info {
    display: flex;
    flex-flow: column nowrap;
    margin-left: 24px;
  }
}

igx-snackbar {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 2;
}

