import { Component, OnInit } from "@angular/core";

@Component({
  selector: "app-tooltip-rich",
  styleUrls: ["./tooltip-rich.component.scss"],
  templateUrl: "./tooltip-rich.component.html"
})
export class TooltipRichComponent implements OnInit {

  constructor() { }

  public ngOnInit() {
  }
}

.wrapper { 
    margin: 20px;
}

.cursorHover:hover {
    cursor: pointer;
}

.card-wrapper {
    max-width: 200px;
    min-width: 120px;
}

.location {
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 200px;
    height: 252px;
    background-image: url(assets/images/card/media/infragisticsMap.png);
}

.locationTooltip {
    width: 310px;
    background-color: rgba(128, 128, 128, 0.7);
    padding: 3px;
    font-size: 13px;
}

.locationTooltipContent {
    display: flex;
    justify-content: center;
    align-items: center;
}

.locationIcon {
    width: 45px;
    height: 45px;
}

import { NgModule } from "@angular/core";
import { FormsModule } from "@angular/forms";
import { BrowserModule } from "@angular/platform-browser";
import { BrowserAnimationsModule } from "@angular/platform-browser/animations";
import { AppComponent } from "./app.component";
import { 
	IgxAvatarModule,
	IgxTooltipModule,
	IgxCardModule,
	IgxIconModule,
	IgxButtonModule
 } from "igniteui-angular";
import { TooltipRichComponent } from "./tooltip-rich/tooltip-rich.component";

@NgModule({
  bootstrap: [AppComponent],
  declarations: [
    AppComponent,
		TooltipRichComponent
  ],
  imports: [
    BrowserModule,
    BrowserAnimationsModule,
    FormsModule,
		IgxAvatarModule,
		IgxTooltipModule,
		IgxCardModule,
		IgxIconModule,
		IgxButtonModule
  ],
  providers: [],
  entryComponents: []
})
export class AppModule {}
